package Practiseques;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int people=10;
		people=people+3;
		System.out.println(people);
		
		
		int mango=12;
		mango=mango%5;
		System.out.println(mango);
		
		
		
		int coc=20;
		coc++;
		System.out.println(coc);
		//++increment
		//--decrement
		

	}

}
