package controlstment;

public class nestedjavaprint {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=1;i<=1;i++)
		{
		for(int j=1;j<=i;j++)	
		{
			System.out.println("*");
		}
		System.out.println("**");
		}
		System.out.println("***");
		{
			System.out.println("****");
		}

	}

}
